function widget_inline(a){var b=widget_findcomment(a,!1);if(!b)return alert("Widget not found");var c=$(b.nodeValue);$(b).replaceWith(c);widget_add(a,c)}function widget_add(a,b){$(a).data("litepublisher_widget",b);$(a).click(function(){widget_toggle(this);return!1})}function widget_toggle(a){$(a).data("litepublisher_widget").slideToggle()}
