var pollclient={cookierequested:false,cookie:"",voted:[]};pollclient.init=function(){$("*[id^='poll_']").click(function(){var a=$(this).attr("id").split("_");pollclient.clickvote(a[1],a[2]);return false});$("form[id^='pollform_radio_']").submit(function(){var b=$(this).attr("id").split("_");var a=$("input:radio:checked",$(this)).val();pollclient.clickvote(b[2],a);return false})};pollclient.sendvote=function(a,b){$.get(ltoptions.url+"/ajaxpollserver.htm",{action:"sendvote",cookie:this.cookie,idpoll:a,vote:b},function(c){var d=c.split(",");var f="#pollresult_"+a+"_";for(var e=0,g=d.length;e<g;e++){$(f+e).html(d[e])}})};pollclient.clickvote=function(a,b){for(var c=this.voted.length-1;c>=0;c--){if(a==this.voted[c]){return false}}this.voted.push(a);if(this.cookierequested){this.sendvote(a,b)}else{this.cookie=get_cookie("polluser");if(this.cookie==null){this.cookie=""}this.getcookie(function(){pollclient.sendvote(a,b)})}};pollclient.getcookie=function(a){$.get(ltoptions.url+"/ajaxpollserver.htm",{action:"getcookie",cookie:this.cookie},function(b){if(b!=pollclient.cookie){set_cookie("polluser",b,false);pollclient.cookie=b}pollclient.cookierequested=true;if($.isFunction(a)){a()}})};