$(document).ready(function(){$("#themetree").find("a").click(function(){try{$(this).parent().find("ul:first").slideToggle();var a=$(this).attr("rel");if(a=="ignore"){return false}var b=$(this).data("editor");if(b){switcheditor(b)}else{$(this).data("editor",createeditor(a,$(this).text()))}}catch(b){alert(b.message)}return false})});function replace_string(b,c,d){var a=b.indexOf(c);while(a>-1){b=b.replace(c,d);a=b.indexOf(c)}return b}function createeditor(d,e){var b='<p><label for="$name"><strong>$lang.$name:</strong></label><br /> <textarea name="$name" id="$name" cols="57%" rows="10">$value</textarea></p>';b=replace_string(b,"$lang.$name",e);b=replace_string(b,"$name",replace_string(d,".","_"));var c=theme[d];if(c==undefined){alert(d)}b=replace_string(b,"$value",c);var a=$(b);$("#themeeditor").append(a);switcheditor(a);return a}function switcheditor(a){var b=$("#themeeditor").data("mycurrent");if(b){if(b==a){return}b.slideUp("fast",function(){a.slideDown("fast")})}else{a.show()}$("#themeeditor").data("mycurrent",a)};